BEGIN TRANSACTION;
CREATE TABLE "HABITATS" ("id" integer,"name" text NOT NULL, "description" TEXT NOT NULL, "picture" TEXT NOT NULL DEFAULT 'NULL', PRIMARY KEY (id));
